<?php

namespace Elementor;

if ( ! defined( 'ABSPATH' ) ) exit; // Exit if accessed directly

class Widget_TommusRhodus_Decorations_Block extends Widget_Base {
	
	//Return Class Name
	public function get_name() {
		return 'tommusrhodus-decorations-block';
	}
	
	//Return Block Title (for blocks list)
	public function get_title() {
		return esc_html__( 'Section Decorations', 'tr-framework' );
	}
	
	//Return Block Icon (for blocks list)
	public function get_icon() {
		return 'eicon-slideshow';
	}
	
	public function get_categories() {
		return [ 'leap-elements' ];
	}

	protected function _register_controls() {
		
		$this->start_controls_section(
			'section_my_custom', [
				'label' => esc_html__( 'Decorations Settings', 'tr-framework' ),
			]
		);		

		$this->end_controls_section();

		$this->start_controls_section(
			'slider_items_section', [
				'label' => __( 'Decorations', 'tr-framework' )
			]
		);

		$repeater = new Repeater();

		$repeater->add_control(
			'layout', [
				'label'   => __( 'Choose a Decoration', 'tr-framework' ),
				'type'    => Controls_Manager::SELECT,
				'default' => 'inline',
				'label_block' => true,
				'options' => [
					'blob_bottom_left'		=> esc_html__( 'Blob, Postion Bottom Left', 'tr-framework' ),
					'lines_bottom_right'	=> esc_html__( 'Lines, Postion Bottom Right', 'tr-framework' ),
					'blob_right'			=> esc_html__( 'Blob, Postion Right', 'tr-framework' ),
					'blob_top_left'			=> esc_html__( 'Blob, Postion Top Left', 'tr-framework' ),
					'blob_outline_left'		=> esc_html__( 'Blob, Postion Left', 'tr-framework' ),
					'lines_right'			=> esc_html__( 'Lines, Postion Right', 'tr-framework' ),
				],
			]
		);

		$this->add_control(
			'list', [
				'label'   => __( 'Slide Content', 'tr-framework' ),
				'type'    => Controls_Manager::REPEATER,
				'fields'  => $repeater->get_controls(),
				'default' => [],
				'title_field' => __( 'Slide Content', 'tr-framework' ),
			]
		);		

		$this->end_controls_section();

	}

